    const quizEl = document.getElementById('quiz');
    const summaryEl = document.getElementById('summary');
    const resetBtn = document.getElementById('resetBtn');

    let correctCount = 0;
    let answeredCount = 0;

    function renderQuiz() {
      quizEl.innerHTML = '';
      correctCount = 0;
      answeredCount = 0;
      QUESTIONS.forEach((q, qidx) => {
        const qWrap = document.createElement('div');
        qWrap.className = 'question';
        qWrap.dataset.qidx = qidx;

        const title = document.createElement('div');
        title.className = 'q-title';
        title.textContent = (qidx + 1) + '. ' + q.text;
        qWrap.appendChild(title);

        const opts = document.createElement('div');
        opts.className = 'options';

        q.options.forEach((optText, oi) => {
          const btn = document.createElement('button');
          btn.type = 'button';
          btn.className = 'opt-btn';
          btn.textContent = String.fromCharCode(65 + oi) + '. ' + optText;
          btn.dataset.optIndex = oi;
          btn.dataset.qIndex = qidx;
          btn.addEventListener('click', onOptionClick);
          opts.appendChild(btn);
        });

        qWrap.appendChild(opts);

        const fb = document.createElement('div');
        fb.className = 'feedback';
        fb.textContent = '';
        qWrap.appendChild(fb);

        quizEl.appendChild(qWrap);
      });

      updateSummary();
    }

    function onOptionClick(e) {
      const btn = e.currentTarget;
      const qIndex = Number(btn.dataset.qIndex);
      const optIndex = Number(btn.dataset.optIndex);
      const q = QUESTIONS[qIndex];
      const qWrap = btn.closest('.question');
      const optsContainer = qWrap.querySelector('.options');
      const feedback = qWrap.querySelector('.feedback');

      if (qWrap.dataset.answered === 'true') return;

      qWrap.dataset.answered = 'true';
      answeredCount++;

      if (optIndex === q.correctIndex) {
        btn.classList.add('correct');
        feedback.textContent = 'Правильно ✓';
        correctCount++;
      } else {
        btn.classList.add('wrong');
        feedback.textContent = 'Неправильно ✗';

        const buttons = optsContainer.querySelectorAll('.opt-btn');
        buttons.forEach(b => {
          const idx = Number(b.dataset.optIndex);
          if (idx === q.correctIndex) {
            b.classList.add('correct');
          }
        });
      }

      const allBtns = optsContainer.querySelectorAll('.opt-btn');
      allBtns.forEach(b => b.disabled = true);

      updateSummary();
    }

    function updateSummary(){
      summaryEl.textContent = `Всего вопросов: ${QUESTIONS.length} · Правильных ответов: ${correctCount}`;
    }

    resetBtn.addEventListener('click', () => {
      const qNodes = document.querySelectorAll('.question');
      qNodes.forEach(n => {
        n.dataset.answered = 'false';
        const fb = n.querySelector('.feedback');
        if (fb) fb.textContent = '';
        const btns = n.querySelectorAll('.opt-btn');
        btns.forEach(b => {
          b.disabled = false;
          b.classList.remove('correct','wrong');
        });
      });
      correctCount = 0;
      answeredCount = 0;
      updateSummary();
    });

    renderQuiz();